/* Dark Comfort for Wikipedia by R3gi, Czech Republic
Version: 2019.11.25.17.20; */

/* Dark Comfort colors */
:root {
    --dc_color_00: transparent;
    --dc_color_01: #E0E0E0;
    --dc_color_02: #263238;
    --dc_color_03: #1E272B;
    --dc_color_04: #455A64;
    --dc_color_05: #607D8B;
    --dc_color_06: #BDBDBD;
    --dc_color_07: #FAFAFA;
    --dc_color_08: #89FF00;
    --dc_color_09: #8BC34A;
    --dc_color_10: #FFC107;
    --dc_color_11: #EC407A;
    --dc_color_12: #F44336;
    --dc_color_13: #FFECB3;
    --dc_color_14: #000000;
    --dc_color_15: #B2DFDB;
    --dc_color_16: #03A9F4;
    --dc_color_17: #546E7A;
    --dc_color_18: #E57373;
    --dc_color_19: #37474F;
    --dc_color_20: #FFEB3B;
    --dc_color_21: #1976D2;
    --dc_color_22: #2196F3;
    --dc_color_23: #424242;
    --dc_color_24: #FF9800;
    --dc_color_25: #FFFFFF;
    --dc_color_26: #1E88E5;
    --dc_color_27: #FDD835;
    --dc_color_28: #64B5F6;
    --dc_color_29: #FBC02D;
    --dc_color_30: #A5D6A7;
    --dc_color_31: #4E342E;
    --dc_color_32: rgba(0,0,0,0.3);
    --dc_color_33: rgba(38, 50, 56, 0.90);
}

#div1 {
    background-color: var(--dc_color_01); 
}


/* GENERAL backround and text color*/
body {
    color: var(--dc_color_01) !important;
    background-color: var(--dc_color_02) !important;
}

/* SCROLLBAR Chromium */

*::-webkit-scrollbar {
    background: var(--dc_color_03) !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    background: var(--dc_color_04) !important;
}
*::-webkit-scrollbar-thumb:hover {
    background: var(--dc_color_05) !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: var(--dc_color_03) !important;
}

/* NO background image*/
div.vectorTabs,
div.vectorTabs ul li,
div.vectorTabs span,
div.vectorMenu h3,
div.portal,
ul,
.client-js #preftoc li.selected a,
.client-js #preftoc li,
.wikiEditor-ui-toolbar,
.cnotice,
.mw-mmv-title-para .mw-mmv-ttf-ellipsis:before,
.mwe-popups .mwe-popups-extract[dir='ltr']:after {
	background-image: none !important;
}

ul {
    list-style-image: none;
}

.labelced_note_text > i > span,
.cx-card--search__icon,
.ext-related-articles-card-list h3:after {
    background-color: var(--dc_color_00) !important;
}

/* SHADOW */
.cx-highlight,
.card,
#header_sitename,
.inactive-menu-tab,
.mw-mmv-ttf-container .mw-mmv-ttf-ellipsis,
.mwe-popups {
	box-shadow: none;
}

.mwe-popups {
	box-shadow: 0 30px 90px -20px var(--dc_color_32), 0 0 1px var(--dc_color_02);
}

/* ICONS INVERSION */
.central-textlogo-wrapper,
.sprite-icons,
.mw-echo-notifications-badge:before,
#pt-userpage, #pt-anonuserpage,
div.vectorMenu h3 a,
#mw-indicator-mw-helplink a,
#p-lang .uls-settings-trigger,
.mwe-math-fallback-image-inline,
/*.oo-ui-icon-article,*/ .mw-ui-icon-article:before,
/*.oo-ui-icon-speechBubbles,*/ .mw-ui-icon-speechBubbles:before,
.oo-ui-buttonElement.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover > .oo-ui-iconElement-icon, .oo-ui-buttonElement.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover > .oo-ui-indicatorElement-indicator,
