@media (min-width: 1024px) {
    .newUI .entry .checkmate {
        margin: 0!important;
        position: absolute!important;
        left: 0;
        top: 0;
    }
    .newUI #installed {
        margin: 1px 0!important;
        height: min-content!important;
    }
    .newUI #installed:after {
        content: ''!important;
        height: 1px!important;
        width: 100%!important;
        background: hsl(0, 0%, 0%)!important;
    }
    .newUI .entry.usercss .style-name-link:after {
        display: none!important;
    }
    .newUI .entry .checkmate input {
        position: relative!important;
    }
    .newUI .entry .style-name-link,
    .newUI .entry .style-name input {
        pointer-events: all!important;
    }
    html.newUI .entry .style-name:hover .style-name-link {
        color: hsl(0, 0%, 86%)!important;
    }
    html.newUI .entry .style-name:hover .style-name-link:hover {
        color: hsl(0, 0%, 100%)!important;
    }
    .newUI .entry .style-name {
        padding-left: 20px!important;
        pointer-events: none;
    }
    .newUI .entry.usercss .style-name {
        padding: 0 32px 0 20px!important;
    }
    html.newUI .entry {
        border-left: 1px solid #000!important;
    }
    html.newUI #stylus-manage .entry {
        border-top: 1px solid #000!important;
        box-shadow: -1px 0 0 0 hsla(0, 0%, 40%, .15), inset 1px 0 0 0 hsla(0, 0%, 40%, .15), inset 0 1px 0 0 hsla(0, 0%, 40%, .3);
    }
    html.newUI #stylus-manage .entry > * {
        border-top: 0!important;
        box-shadow: none!important;
    }
    .newUI .entry input[type="checkbox"]:not(.slider):checked + .svg-icon.checked {
        top: 2px!important;
    }
    .newUI .entry.usercss .style-name:before,
    .newUI .entry.odd.usercss .style-name:before {
        display: block!important;
        right: 0!important;
        top: 0!important;
        left: unset!important;
        bottom: unset!important;
        width: min-content;
        position: absolute!important;
        margin: 1px 4px 0!important;
        padding: 1px 3px!important;
        border-radius: 4px!important;
        font-size: 11px!important;
        font-weight: bold!important;
        text-indent: 0!important;
        letter-spacing: .5px;
        content: "UC";
        text-transform: uppercase!important;
        background-color: hsla(180, 100%, 20%, 1)!important;
        opacity: 1!important;
        color: hsl(0, 0%, 90%)!important;
